                "displayHoles": null,
                "about": null,
                "answers": "[{\"text\":\"<p>Pour supprimer les donn\\u00e9es inutiles<\\/p>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<p>Pour les convertir en entit\\u00e9s HTML<\\/p>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<p>Pour prot\\u00e9ger son site Web contre les attaques telle que les injections SQL<\\/p>\",\"comment\":\"\",\"is_good\":1},{\"text\":\"<p>Pour enlever les balises HTML<\\/p>\",\"comment\":\"\",\"is_good\":0}]"
