                "about": null,
                "answers": "[{\"text\":\"<pre><code class=\\\"language-php\\\">if (false == is_email($email, FILTER_VALIDATE_EMAIL)) {\\n    exit('Votre email n'est pas valide);\\n}<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code class=\\\"language-php\\\">if (false == $email)) {\\n    exit('Votre email n'est pas valide);\\n}<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code class=\\\"language-php\\\">if (false == filter_var($email, FILTER_VALIDATE_EMAIL)) {\\n    exit('Votre email n'est pas valide);\\n}<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":1},{\"text\":\"<pre><code class=\\\"language-php\\\">if (false == strpos($email, '@')) {\\n    exit('Votre email n'est pas valide);\\n}<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0}]"
