                "answers": "[{\"text\":\"<pre><code>&lt;?php\\n    $firstname = $person['firstname'];\\n    $lastname = $person['lastname'];\\n    $civ = $person['civ'];\\n?&gt;\\n&lt;ul&gt;\\n    &lt;li&gt;&lt;?php echo $firstname ?&gt;&lt;\\/li&gt;\\n    &lt;li&gt;&lt;?php echo $lastname ?&gt;&lt;\\/li&gt;\\n    &lt;li&gt;&lt;?php echo $civ ?&gt;&lt;\\/li&gt;\\n&lt;\\/ul&gt;<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code>&lt;ul&gt;\\n    &lt;li&gt;\\n        &lt;?php\\n            for($i = 0; $i < count($person); $i++) {\\n                $person[$i];\\n            }\\n        ?&gt;\\n    &lt;\\/li&gt;\\n&lt;\\/ul&gt;<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code>&lt;ul&gt;\\n&lt;?php foreach($persons as $person) ?&gt;\\n    &lt;li&gt;\\n        &lt;?php echo $person ?&gt;\\n    &lt;\\/li&gt;\\n&lt;?php endforeach ?&gt;\\n&lt;\\/ul&gt;<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":1}]"
