                "answers": "[{\"text\":\"<pre><code>$.ajax({\\n    url : 'page.php'\\n}).done(f1).fail(f2);<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":1},{\"text\":\"<pre><code>$.ajax({\\n    url : 'page.php'\\n}).success(f1).error(f2);<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":1},{\"text\":\"<pre><code>$.ajax({\\n    url : 'page.php'\\n}).ok(f1).nok(f2);<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code>$.ajax({\\n    url : 'page.php',\\n    good : f1,\\n    error : f2\\n})<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0}]"
