                "answers": "[{\"text\":\"<pre><code class=\\\"language-php\\\">$newVar = $oldVar ?? 'valeur_par_defaut';<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":1},{\"text\":\"<pre><code class=\\\"language-php\\\">$newVar = $oldVar &lt;=&gt; 'valeurpardefaut';<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code class=\\\"language-php\\\">$var = $var :: 'valeurpardefaut';<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code class=\\\"language-php\\\">$var = $var :: 'valeurpardefaut';<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code class=\\\"language-php\\\">$newVar = $oldVar :: 'valeurpardefaut';<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code class=\\\"language-php\\\">$newVar = $oldVar :? 'valeurpardefaut';<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0}]"
