                "displayHoles": null,
                "about": null,
                "answers": "[{\"text\":\"<p>Signale \\u00e0 l'utilisateur que la ressource est indisponible.<\\/p>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<p>Signale \\u00e0 l'utilisateur la page demand\\u00e9e est en travaux.<\\/p>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<p>Signale \\u00e0 l'utilisateur que la ressource n'existe pas.<\\/p>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<p>Signale \\u00e0 l'utilisateur qu'il tente d'acc\\u00e9der \\u00e0 une ressource interdite.<\\/p>\",\"comment\":\"\",\"is_good\":1}]"
