                "about": null,
                "answers": "[{\"text\":\"<pre><code>SELECT name, age\\nFROM People\\nWHERE age &gt; (\\n     SELECT AVG(age)\\n     FROM People\\n);<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":1},{\"text\":\"<pre><code>SELECT name, age\\nFROM People\\nWHERE age &gt; (\\n     SELECT SUM(age)\\n     FROM People\\n);<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0},{\"text\":\"<pre><code>SELECT age &gt; (\\n     SELECT AVG(age)\\n     FROM People\\n)\\nFROM People;<\\/code><\\/pre>\",\"comment\":\"\",\"is_good\":0}]"
